import React from "react";
import restaurants from "../sample-restaurants";
import { useState } from "react";

const Landing = (props) => {
  const [state, setState] = useState("");
  const [title, setTitle] = useState("");
  const [url, setUrl] = useState("");
  const [display, toggleDisplay] = useState(false);

  //   state = {
  //     display: false,
  //     title: "",
  //     url: "",
  //   };

  const displayList = () => {
    toggleDisplay(!display);
  };

  const getTitle = (restaurant) => {
    const { title, url } = restaurant;
    setTitle(title);
    setUrl(url);
    toggleDisplay(false);
  };

  const goToRestaurant = () => {
    props.history.push(`/restaurant/${url}`);
  };

  return (
    <div className="restaurant-select">
      <div className="restaurant-select-top">
        <div
          className="restaurant-select-top-header font-effect-outline"
          onClick={displayList}
        >
          {state.title ? state.title : "Choose the restaurant"}
        </div>
        <div className="arrow-picker">
          <div className="arrow-picker-up"></div>
          <div className="arrow-picker-down"></div>
        </div>
      </div>

      {state.display ? (
        <div className="restaurant_select_bottom">
          <ul>
            {restaurants.map((restaurant) => {
              return (
                <li onClick={() => getTitle()} key={restaurant.id}>
                  {restaurant.title}
                </li>
              );
            })}
          </ul>
        </div>
      ) : null}

      {title && !display ? (
        <button onClick={goToRestaurant}>Go to restaurant </button>
      ) : null}
    </div>
  );
};
export default Landing;
