import React from "react";
import restaurants from "../sample-restaurants";

class Landing extends React.Component {
  state = {
    display: false,
    title: "",
    url: "",
  };

  displayList = () => {
    const { display } = this.state;
    this.setState({ display: !display });
  };

  getTitle = (restaurant) => {
    const { title, url } = restaurant;
    this.setState({ title, url, display: false });
  };

  render() {
    return (
      <div className="restaurant-select">
        <div className="restaurant-select-top">
          <div
            className="restaurant-select-top-header font-effect-outline"
            onClick={this.displayList}
          >
            {this.state.title ? this.state.title : "Choose the restaurant"}
          </div>
          <div className="arrow-picker">
            <div className="arrow-picker-up"></div>
            <div className="arrow-picker-down"></div>
          </div>
        </div>

        {this.state.display ? (
          <div className="restaurant_select_bottom">
            <ul>
              {restaurants.map((restaurant) => {
                return (
                  <li onClick={() => this.getTitle()} key={restaurant.id}>
                    {restaurant.title}
                  </li>
                );
              })}
            </ul>
          </div>
        ) : null}

        <button>Go to restaurant</button>
      </div>
    );
  }
}

export default Landing;
